import { Component } from '@angular/core';

import { products } from '../products';

@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.css']
})
export class ProductListComponent {
  products = products;

  share(product: {kaspi: string}) {
    window.open('https://telegram.me/share/url?url=' + product.kaspi)
  }
  
  onNotify() {
    window.alert('You will be notified when the product goes on sale');
  }

  stars(num: number, check: boolean): string  {
    Element.a
    for (let i = 0; i < num; i++) {
        if(check){
            document.querySelector('.rating')?.insertAdjacentHTML('beforeend', '<img src="https://www.freepnglogos.com/uploads/star-png/star-vector-png-transparent-image-pngpix-21.png" class="star">');
        } else {
            text += '☆'
        }
    }
    return text;
  }
}


