import { Component, Input } from '@angular/core';
import { Product, products } from '../products';

@Component({
  selector: 'app-product-item',
  templateUrl: './product-item.component.html',
  styleUrls: ['./product-item.component.css']
})
export class ProductItemComponent {
  @Input() product: Product | undefined;

  status : boolean = false

  share(product: {kaspi: string}): void {
    window.open('https://telegram.me/share/url?url=' + product.kaspi)
  }

  onNotify() {
    window.alert('You will be notified when the product goes on sale');
    console.log(this.product?.description.length);
  }

  stars(num: number, check: boolean): string  {
    var text = "";
    for (let i = 0; i < num; i++) {
        if(check){
            text += '⭐️';
        } else {
            text += '☆'
        }
    }
    return text;
  }

  // increase(product: {likes: number}) {
  //   product.likes += 1;
  // }
  // decrease(product: {likes: number}) {
  //   product.likes -= 1;
  // }
  // likes(product: {likes: number}, status: boolean) {
  //   if(status) product.likes++;
  //   else if(!status) {
  //     product.likes--;
  //   }
  // }
  increase() {
    if(this.product !== undefined) {
      if(!this.status) this.product.likes++;
      else this.product.likes--;
      this.status = !this.status
    }
  }

}
